package com.syntax.class03;

public class ShorthandOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//+,-,/,% 
		
		int num=100;
		num=num+100;
		System.out.println(num);
		
		num+=100;
		System.out.println(num);
		num-=20;
		System.out.println(num);
		num/=10;
		System.out.println(num);
		
		num%=5;
		System.out.println(num);
	}

}
